import React from "react";
import { StyleSheet, View, Text } from "react-native";

const WeatherItem = ({title, value, unit}) => {
    return(
        <View style={styles.weatherItem}>
            <Text style={styles.weatherItemTitle}>{title}</Text>
            <Text style={styles.weatherItemValue}>{value}{unit}</Text>
        </View>
    )
}

const DateTime = () => {
  return (
    <View style={styles.container}>
      <View>
        <View>
          <Text style={styles.heading}>12:12</Text>
        </View>
        <View>
          <Text style={styles.subHeading}>Vendredi 30 Juin</Text>
        </View>
        <View style={styles.weatherItemContainer}>
            <WeatherItem title="HumiditÃ©" value="79" unit="%"/>    
            <WeatherItem title="Pression" value="1000" unit="hp"/>  
            <WeatherItem title="Sunrise" value="06:34"/>  
            <WeatherItem title="Sunset" value="20:43"/>  
            </View>
      </View>
      <View style={styles.rightAlign}>
        <Text style={styles.timeZone}>France, Saint-Nazaire</Text>
        <Text style={styles.latLong}>4.22N 50E</Text>
      </View>
    </View>
  );
};


const styles = StyleSheet.create({
    container: {
        flex:1.5,
        flexDirection: "row",
        justifyContent: "space-between",
    },
    heading: {
        fontSize: 45,
        color: 'white',
        marginTop: 25,
        marginLeft: 5,
        fontWeight: '100',
    },
    subHeading: {
        fontSize: 25,
        color: '#eee',
        fontWeight: '300',
        marginLeft: 10,
    },
    rightAlign: {
        textAlign: 'right',
        marginTop: 35,
    },
    timeZone: {
        fontSize: 15,
        color: 'white',
    },
    latLong: {
        fontSize: 16,
        color: 'white',
        fontWeight: '700',
    },
    weatherItemContainer: {
        backgroundColor: "#18181b99",
        borderRadius: 10,
        marginLeft: 10,
        padding: 10,
        marginTop: 10,
    },
    weatherItem: {
        flexDirection: "row",
        justifyContent: "space-between",
    },
    
})

export default DateTime;
